var Enemy=function(e){this.sprite="images/enemy-bug.png",this.speed=256,this.x=0,this.y=e};Enemy.prototype.update=function(e){this.x=this.x+this.speed*e,this.x>505&&(this.x=-100)},Enemy.prototype.render=function(){ctx.drawImage(Resources.get(this.sprite),this.x,this.y)};var Hero=function(){this.sprite="images/char-cat-girl.png",this.speed=100,this.x=200,this.y=400,this.hitDetect=function(){for(i=0;i<allEnemies.length;i++)this.y<=allEnemies[i].y+50&&this.x<=allEnemies[i].x+50&&(this.x=200,this.y=400,console.log("it ran"))}};Hero.prototype.update=function(e){(this.y>400||this.y<0)&&(this.y=400,this.x=200),(this.x>400||this.x<0)&&(this.x=200),player.hitDetect()},Hero.prototype.render=function(){ctx.drawImage(Resources.get(this.sprite),this.x,this.y)},Hero.prototype.handleInput=function(e){"up"===e&&(this.y-=25),"down"===e&&(this.y+=25),"left"===e&&(this.x-=100),"right"===e&&(this.x+=100)};var allEnemies=[],player=new Hero,beetle1=new Enemy(65),beetle2=new Enemy(145),beetle3=new Enemy(225);allEnemies.push(beetle1,beetle2,beetle3),document.addEventListener("keyup",function(e){var t={37:"left",38:"up",39:"right",40:"down"};player.handleInput(t[e.keyCode])});